#ifndef __ERRCODES_H__
#define __ERRCODES_H__

/* --Error codes-- */

#define ERRCODE_OK 1

#define ERRCODE_FILE_IO   -1  /* Problems reading/writing file */
#define ERRCODE_FILE_DATA -2  /* Bad file contents */
#define ERRCODE_MALLOC    -3  /* Can't get enough memory */
#define ERRCODE_NO_EXTN   -4  /* Requested MEF extension doesn't exist */
#define ERRCODE_NO_SUCH_ROW -5 /* Requested row doesn't exist */
#define ERRCODE_BADRES      -6 /* Bad moments result */
#define ERRCODE_UNDEF_OPT   -7 /* Undefined option */

/* Maximum length of an error message */

#define ERRSTR_LEN 256

#endif  /* __ERRCODES_H__ */
